import com.rameses.rcp.common.*
import com.rameses.rcp.annotations.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*

class BankListController extends ListController
{
    String serviceName = 'BankListService'
    String entityName  = 'bank'

    Column[] getColumns(){
        return [
            new Column(name:"code", caption:"Bank Code"),
            new Column(name:"name", caption:"Bank Name"),
            new Column(name:"branchname", caption:"Branch Name"),
        ] as Column[]
    }
}
