workunit.code:

import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*
import java.rmi.server.*
import com.rameses.gov.etracs.bpls.application.*;

class  BPRequirementDataController {

    @Service("BPApplicationService")
    def service;

    def handler;
    def entity;
    def info = [:];

    void init() {
        info.objid = "BPREQDATA"+new UID();
        info.refid = entity.objid;
    }

    def doOk() {
        def z = service.submitDataRequirement( info );
        handler(z);
        return "_close";
    }

    def doCancel() {
        return "_close";
    }

}
