workunit.code:

import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*
import java.rmi.server.*
import com.rameses.gov.etracs.bpls.application.*;

class  AddBusinessReceivableController {

    def business;
    def entity;
    def handler;
    def lobList;

    def paymentModes = ["YEAR", "QTR"];

    def getLookupAccounts() {
        return InvokerUtil.lookupOpener( "revenueitem:lookup", [
            onselect : { o->
                entity.account = o;
            }   
        ]);
    }

    void createNew() {
        entity = [:]
        entity.objid = "BPRCV"+new UID();
        entity.amtdue = 0;
        entity.amtpaid = 0;
        entity.balance = 0;
        entity.discount = 0;
        entity.businessid = business.objid;
    }

    void addAndContinue() {
        if(entity.iyear< 1950 || entity.iyear > 2015)
            throw new Exception("Year is invalid");
        if(entity.iqtr && entity.iqtr >= 1 && entity.iqtr <= 4 )
            throw new Exception("Qtr is invalid");  
        if(entity.amount < 0) 
            throw new Exception("Amount must be greater than 0");  
        entity.balance = entity.amount;    
        handler(entity);
        createNew();
    }

    def addAndClose() {
        addAndContinue();
        createNew();
        return "_close";
    }

    def doClose() {
        return "_close";
    }
}
