workunit.code:

import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*
import java.rmi.server.*;
import com.rameses.gov.etracs.bpls.application.*;

class BPApplicationExtOfficeAddFeeController  {

    def handler;
    def department;
    def refid;
    def reftype;
    def entity = [:];

    void init() {
        entity = [:];
    }

     def getLookupAccount() {
        return InvokerUtil.lookupOpener("businessaccount:lookup", [
            "query.department": department,
            onselect: { o->   
                entity.account = o;
                entity.taxfeetype = o.taxfeetype;
            }
        ]);
    }

    void addItem() {
        entity.department = department;
        entity.refid = refid;
        entity.reftype = reftype; 
        handler(entity);
        entity = [:];
    }

    def doClose() {
        return "_close";
    }


}
