workunit.code:

import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*
import java.rmi.server.*;
 import com.rameses.osiris2.reports.*;

class  BPUnifiedFormReportController  {

    def data;
    def entity;
    def title = "BP Application"

    def reportPath = "com/rameses/gov/etracs/bpls/reports/unifiedform/"
    def orgtypes = LOV.ORG_TYPES;    

    @Service("ReportParameterService") 
    def paramsvc

    @FormId
    String formId;

    @FormTitle
    String formTitle;

    void viewReport() {
        formTitle = "App "+ entity.appno; 
        formId = "APPRPT:"+entity.objid;

        def format = { x->
            if(x==null) return "";
            else if(x==true) return "Yes";
            else if(x==false) return "No";
            else return x+"";
        }
        def infos = entity.infos.collect{ 
            [
              category:it.lob?.name, 
              name: it.attribute.caption,
              value: format(it.value)
            ]  
         };



        //ctcno: "CCI2012-191919191", 
        //ctcdate: java.sql.Date.valueOf("2013-02-01"),
        data = [
           apptype: entity.apptype,
           refno: entity.appno, 
           appdate: entity.dtfiled,
           tradename: entity.tradename, 
           businessorgtype: orgtypes.find{ it.key == entity.orgtype}?.value,
           businessaddress: entity.businessaddress,
           ownername: entity.owner.name, 
           ownerno: entity.owner.entityno,
           owneraddress: entity.owner.address,
           txndate: entity.dtfiled, 
           laterenewal:"0", 
           infos:infos,
           requierements:[],
           signatories:[],
           barcode: "51005:"+entity.appno
        ]
        report.viewReport();
    } 

    def report = [
        getReportName : { return reportPath + "main.jasper" },
        getReportData : { return  data },
        getParameters : { return paramsvc.getStandardParameter() },
        getSubReports : { return [
            new SubReport("ITEM", reportPath + "item.jasper")
        ] as SubReport[]; }
    ] as ReportModel

}
