workunit.code:

import com.rameses.rcp.common.*
import com.rameses.rcp.annotations.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*
import com.rameses.rulemgmt.constraint.*;
import com.rameses.rulemgmt.*;
import java.rmi.server.*;
import com.rameses.gov.etracs.bpls.*;

class BPAssessmentAnalyzerInfoController  {
    @Binding
    def binding;

    def lobs;
    def handler;
    def formInfos;

    def formPanel = [
        getCategory: { lobid->
            if(!lobid) return "";
            return lobs.find{ it.lobid == lobid }?.name
        },
        updateBean: {name,value,item->
            item.bean.value = value;
        },
        getControlList: {
            return formInfos;
        }
    ] as FormPanelModel;

    def doOk() {
        formInfos = handler();
        if(!formInfos) 
            return "_close";
        else    
            binding.refresh();
    }

    def doCancel() {
        return "_close";
    }
}
