workunit.code:

import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*
import java.rmi.server.*
import com.rameses.gov.etracs.bpls.business.*;

class  BusinessInfoController extends AbstractBusinessController {

    @Service("BusinessInfoService")
    def service;

    String title = "Business Information";
    def mode = "read";
    def entityName = "business";

    def create() {
        entity = [objid: "BUS"+new UID(), lobs: []];
        entity.state = 'INACTIVE';
        mode="create";
        return "new";
    }

    def open() {
        entity = service.open(entity);
        mode="read";
        return "info";
    }

    def save() {
        entity = service.create(entity);
        mode = "read";
        return "info";
    }
}
