workunit.code:

import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*
import java.rmi.server.*
import com.rameses.gov.etracs.bpls.business.*;

class  BusinessGeneralInfoController  {

    String title = "General Info";

    def entity;

    def showChangeTypes(def inv) {
        def list = InvokerUtil.lookupOpeners("business:generalinfo:changetype", [entity:entity]);
        def popupMenu = new PopupMenuOpener();
        list.each {
            popupMenu.add(it); 
        }
        return popupMenu;
    }

}
