import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.common.*
import com.rameses.osiris2.reports.*;

class BPLSAssessmentPrintOut extends ReportModel{

    @Service('BusinessAssessmentReportService')
    def svc;

    def entity;

    public Map getParameters() {
        //return svcParams.getStandardParameter( OsirisContext.env.USERNAME );
        return;
    }

    public Object getReportData() {
        return svc.buildAssessmentReportInfo( entity );
    }

    public String getReportName() {
        return 'com/rameses/gov/etracs2/bpls/reports/assessment/Assessment.jasper';
    }

    public SubReport[] getSubReports(){
        return [
            new SubReport("ASSESSMENTITEM","com/rameses/gov/etracs2/bpls/reports/assessment/AssessmentItem.jasper"),
            new SubReport("ASSESSMENTNONTAX","com/rameses/gov/etracs2/bpls/reports/assessment/AssessmentNonTax.jasper"),
            new SubReport("ASSESSMENTTAX","com/rameses/gov/etracs2/bpls/reports/assessment/AssessmentTax.jasper"),
            new SubReport("SIGNATORYITEM","com/rameses/gov/etracs2/bpls/reports/assessment/SignatoryItem.jasper")
        ] as SubReport[];    
    }   
}
