import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.common.*
import com.rameses.osiris2.reports.*;

class BPLSApplicationPrintOut extends ReportModel {


    @Service('BusinessApplicationReportService')
    def svc;

    def entity;

    public Map getParameters() {
        //return svcParams.getStandardParameter( OsirisContext.env.USERNAME );
        return;
    }

    public Object getReportData() {
        return svc.buildApplicationReportInfo( entity );
    }

    public String getReportName() {
        return 'com/rameses/gov/etracs2/bpls/reports/application/bppermitapplication.jasper';
    }

    public SubReport[] getSubReports(){
        return [
            new SubReport("APPINFO","com/rameses/gov/etracs2/bpls/reports/application/appinfo.jasper"),
            new SubReport("APPINFOITEM","com/rameses/gov/etracs2/bpls/reports/application/appinfoitem.jasper"),
            new SubReport("REQUIREMENT","com/rameses/gov/etracs2/bpls/reports/application/requirement.jasper"),
            new SubReport("SIGNATORIES","com/rameses/gov/etracs2/bpls/reports/application/signatories.jasper")
        ] as SubReport[];    
    }   
}
