import com.rameses.rcp.common.*
import com.rameses.rcp.annotations.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*

class ListVariableController extends ListController
{
    String serviceName = 'VariableListService'
    String entityName = 'variable'

    Column[] getColumns(){
        return [
            new Column(name:"name", caption:"Name"),
            new Column(name:"caption", caption:"Caption"),
            new Column(name:"datatype", caption:"Data Type"),
        ]
    }
}
