workunit.code:

import com.rameses.rcp.common.*
import com.rameses.rcp.annotations.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*
import com.rameses.rulemgmt.constraint.*;
import com.rameses.rulemgmt.*;
import java.rmi.server.*;
import com.rameses.gov.etracs.bpls.*;

class BPAssessmentAnalyzerController extends BPApplication {

    String title = "Business Rule Assessment Simulator";

    String entityName = "bpassessment:analyzer";

    void init() {
        lobAssessmentTypes = LOV.LOB_ASSESSMENT_TYPES*.key;
    }

    def getInfo() {
        loadInfos();
        //pass to the looper
        def opener = InvokerUtil.lookupOpener( "bpassessment:info",
              [ formInfos:formInfos,
                lobs:entity.lobs,
                handler:{   
                    addInfos();
                    loadInfos();
                    if(infos) 
                        return formInfos;
                    else
                        return null;
               }]
        );    
        opener.target = "popup";
        return opener;
    }

    def getTaxFees() {
        infos = entity.infos.findAll{ it.phase != 0};
        buildFormInfos();
        def opener = InvokerUtil.lookupOpener( "bpassessment:info",
            [formInfos:formInfos, lobs:entity.lobs,
                handler:{
                    calculateTaxfees();
                }
            ]
        );   
        opener.target = "popup";
        return opener;
    }

    void reset() {
        initNew();
    }


}
