import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*

class MTOPRouteListController extends ListController
{
    String serviceName = 'MTOPRouteListService'
    String entityName  = 'route'

    Column[] getColumns() {
        return [
            new Column(name:"state", caption:"State", maxWidth:120, width:120,),
            new Column(name:"code", caption:"Code"),
            new Column(name:"description", caption:"Description")
        ]
    }
}
