workunit.code:

import com.rameses.rcp.annotations.*
import com.rameses.rcp.common.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*

class MTOPModelMasterController extends CRUDController
{
    @Service('MTOPMakeListService')
    def makeSvc

    @Service('MTOPUnitTypeListService')
    def unittypeSvc

    String serviceName = 'MTOPModelMasterService'
    String entityName = 'model'
    String prefixId = 'MO'

    def make = [:]
    def unittype = [:]

    void open() {
        super.open()
        make = getMakeList().find{ it.objid == entity.makeid }
        unittype = getUnittypeList().find{ it.objid == entity.unittypeid }
    }

    void save() {
        entity.makeid     = make.objid
        entity.make       = make.name
        entity.unittypeid = unittype.objid
        entity.unittype   = unittype.name
        super.save()
    }

    List getMakeList() {
        return makeSvc.getApprovedList()
    }

    List getUnittypeList() {
        return unittypeSvc.getApprovedList()
    }
}
