workunit.code:

import com.rameses.rcp.annotations.* 
import com.rameses.rcp.common.* 
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*

class ExaminationFindingsInfoController
{
    @Binding
    def binding;

    @Service('ExaminationService')
    def examinationSvc;

    def svc;


    def entity;
    def finding;
    def mode; 

    def MODE_READ = 'read';
    def MODE_EDIT = 'edit';

    String title = 'Examination Findings'

    void init(){
        finding = examinationSvc.findFindingById(entity.objid)
        if (!finding){
            finding = [objid:entity.objid, isnew:true];
        }
        mode = MODE_READ;
    }


    void edit(){
        mode = MODE_EDIT;
    }

    void save(){
        examinationSvc.save(finding);
        mode = MODE_READ;
    }

    void refresh(){
        init();
    }

    boolean getShowEdit(){
        if (entity.state.matches('CURRENT|CANCELLED|APPROVED'))
            return false;
        if (entity.state != 'FOREXAMINATION')
            return false;
        if (mode != MODE_READ) 
            return false;
        return true;
    }

}
