import com.rameses.rcp.common.*
import com.rameses.rcp.annotations.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*

class MachineListController extends ListController
{
    String serviceName = 'MachineListService'
    String entityName  = 'machine'

    Column[] getColumns(){
        return [
            new Column(name:"code", caption:"Code", width:80),
            new Column(name:"name", caption:"Description", width:250),
            new Column(name:"state", caption:"State", width:100),
        ]
    }
}
