import com.rameses.rcp.common.*
import com.rameses.rcp.annotations.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*

class MaterialListController extends ListController
{
    String serviceName = 'MaterialListService'
    String entityName = 'material'

    Column[] getColumns(){
        return [
            new Column(name:"code", caption:"Code", width:80),
            new Column(name:"name", caption:"Material", width:250),
            new Column(name:"state", caption:"State", width:100),
        ]
    }
}
