import com.rameses.rcp.common.*
import com.rameses.rcp.annotations.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*

class PlantTreeListController extends ListController
{
    String serviceName = 'PlantTreeListService'
    String entityName  = 'planttree'

    Column[] getColumns(){
        return [
            new Column(name:"code", caption:"Code", width:80),
            new Column(name:"name", caption:"Plant/Tree", width:250),
            new Column(name:"state", caption:"State", width:100),
        ]
    }
}
