workunit.code:
import com.rameses.rcp.common.*
import com.rameses.rcp.annotations.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*

class RPTTaxpayerLookupController extends LookupController
{
    @Service("RPTTaxpayerLookupService")
    def svc

    Column[] getColumns() {
        return [
            new Column(name:"acctno", caption:"Acct No.", width:80),
            new Column(name:"name", caption:"Taxpayer Name", width:250),
            new Column(name:"address", caption:"Address", width:250),
        ]
    }

    List fetchList( Map params ) { 
        return svc.lookupTaxpayers(params)
    }
}
