import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;

class ComparativeAVReportController extends com.rameses.gov.etracs.rpt.report.ReportController
{
    @Service('RPTReportService') 
    def svc 

    String title = 'Comparative Data on Assessed Value'

    String reportName = 'com/rameses/gov/etracs/rpt/report/comparativeav.jasper';
    def data 


    def getReportData(){
        data = svc.generateComparativeAV(entity)
        return data.reportdata;
    }

    Map getParameters(){
        return [
            TITLE       : data?.title, 
            PERIOD      : data?.period,
            PERIODTYPE  : data?.periodtype,
        ]
    }


   def formControl = [
        getFormControls: {
            return [
                new FormControl( "integer", [caption:'Year', name:'entity.year', required:true, preferredSize:'100,19']),
                new FormControl( "combo", [caption:'Quarter', name:'entity.qtr', items:'quarters', required:true, immediate:true, preferredSize:'100,19']),
                new FormControl( "combo", [caption:'Month', name:'entity.month', items:'months', depends:'entity.qtr', expression:'#{item.name}', dynamic:true, preferredSize:'100,19']),
            ]    
        },
   ] as FormPanelModel;
}
