workunit.code:



import com.rameses.gov.etracs.rpt.report.landtax.RPTDelinquencyTask;
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;

class RPTDelinquencyListingController extends com.rameses.gov.etracs.rpt.report.ReportController
{
    @Service('RPTReportDelinquencyService') 
    def svc

    String title = 'Realty Tax Delinquency Listing';

    String reportName = 'com/rameses/gov/etracs/rpt/report/landtax/rptdelinquency.jasper';
    def data;

    def task;
    def msg;

    def getReportData(){
        return svc.generateDelinquencyReport(entity.barangay);
    }

    Map getParameters(){
        return [
            TITLE : 'Realty Tax Delinquency Listing for ' + entity.barangay.name,
            BARANGAY : entity.barangay.name,
        ]
    }


   def formControl = [
        getFormControls: {
            return [
                new FormControl( "combo", [caption:'Barangay', name:'entity.barangay', items:'barangays', required:true, expression:'#{item.name}', preferredSize:'0,21']),
                new FormControl( "checkbox", [caption:'Rebuild Data', name:'entity.rebuild', preferredSize:'0,21']),
            ]    
        },
   ] as FormPanelModel;


    def preview() {
        if (entity.rebuild ){
            rebuild();
            //return 'default';
        }
        else {
            buildReport()
            mode = 'view'
            return 'preview' 
        }
    }


    void print(){
        if (entity.rebuild ){
            rebuild();
        }
        buildReport()
        ReportUtil.print( report.report, true )
    }

    def oncomplete = {
        entity.rebuild = false;
        buildReport();
        mode = 'view';
        binding.fireNavigation('preview');
    }

    def updateStatus = {rl ->
        msg = 'Calculating ledger ' + rl.tdno + '.'
        binding.refresh('msg');
    }

    void rebuild(){
        if ( entity.rebuild == false ) 
            return;

        entity.currentdate = dtSvc.serverDate;    
        task = new RPTDelinquencyTask([
            svc         : svc,
            entity      : entity,
            oncomplete  : oncomplete,
            updateStatus : updateStatus,
        ]);

        Thread t = new Thread(task);
        t.start();

        mode = 'building';
    }


    void doCancel(){
        svc.cleanup(entity.barangay);
        task.cancelled = true;
        mode = 'init';
        msg = null;
    }
}
