import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.reports.*;

class RPTDelinquencyListingController extends com.rameses.gov.etracs.rpt.report.ReportController
{
    @Service('LandTaxReportService') 
    def svc

    String title = 'Realty Tax Delinquency Listing';

    String reportName = 'com/rameses/gov/etracs/rpt/report/landtax/rptdelinquency.jasper'
    def data 

    def getReportData(){
        return svc.generateDelinquencyReport(entity);
    }

    Map getParameters(){
        return [
            TITLE : 'Realty Tax Delinquency Listing for ' + entity.barangay.name
        ]
    }


   def formControl = [
        getFormControls: {
            return [
                new FormControl( "combo", [caption:'Barangay', name:'entity.barangay', items:'barangays', expression:'#{item.name}', preferredSize:'0,21']),
            ]    
        },
   ] as FormPanelModel;


}
