workunit.code:
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import com.rameses.osiris2.common.*
import com.rameses.gov.etracs.rpt.common.*;
import javax.swing.JFileChooser;


public class RPTRequirementTypeDefaultHandler 
{
    def svc;   // RPTRequirementService

    @Service('DBImageService')
    def imageSvc;

    def entity;

    def MODE_READ = 'read';
    def MODE_EDIT = 'edit';

    def mode; 
    def images;
    def selectedItem;


    @PropertyChangeListener 
    def listener = [
        'entity.value.txnno' : { o ->
            entity.complied = 0;
            if (o) entity.complied = 1;
        }
    ]

    void init(){
        if ( ! entity.value )
            entity.value = [:];
        loadImages();    
        mode = MODE_READ;
    }

    void edit(){
        mode = MODE_EDIT;
    }

    void update(){
        svc.update(entity);
        mode = MODE_READ;
    }

    public String getCaption(){
        return entity.requirementtype.name + ' Detail'
    }

    void loadImages(){
        images = [];
        if (imageSvc){
            images = imageSvc.getImages([refid:entity.objid]);
        }
        listHandler?.load();
    }




    def listHandler = [
        fetchList : { return images },
    ] as BasicListModel


    def addImage(){
        return InvokerUtil.lookupOpener('upload:image', [
            entity : entity,

            afterupload: {
                loadImages();
            }
        ]);
    }

    void deleteImage(){
        if (!selectedItem) return;
        if (MsgBox.confirm('Delete selected image?')){
            DBImageUtil.getInstance(imageSvc).deleteImage(selectedItem.objid);
            loadImages();
        }
    }


    def viewImage(){
        if (!selectedItem) return null;
        return InvokerUtil.lookupOpener('image:view', [
            entity : selectedItem,
        ]);
    }
}
