workunit.code:

import com.rameses.rcp.annotations.* 
import com.rameses.rcp.common.* 
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*

class SignatoryInfoController
{
    @Binding
    def binding;

    def svc;

    def entity;

    String title = 'Signatories'

    void init(){
        listHandler?.load();
    }

    void refresh(){
        init();
    }


    def listHandler = [
        fetchList : { return entity.signatories },
        getColumns : { return [
            new Column(name:'type', caption:'Type'),
            new Column(name:'name', caption:'Name'),
            new Column(name:'title', caption:'Title'),
            new Column(name:'dtsigned', caption:'Date Signed', format:'MM-dd-yyyy', type:'date'),
        ] }
    ] as BasicListModel

}
