        DBImageUtil.getInstance().deleteAllImages(header.refid);
        MsgBox.alert('Success.');
    }

    void saveToFile(){
        DBImageUtil.getInstance().saveToFile(header.objid, targetfile);
        MsgBox.alert('Success.');
    }

