workunit.code:
import com.rameses.rcp.common.*
import com.rameses.rcp.annotations.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*

class AFLookupController extends LookupController
{
    @Service('AFLookupService')
    def svc

    Column[] getColumns() {
        return [
            new Column(name:"objid", caption:"AF Id"),
            new Column(name:"description", caption:"Description"),
            new Column(name:"aftype", caption:"AF Type")
        ]
    }

    List fetchList(Map params) {
        return svc.getList( params )
    }
}
