import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*

class RIVLookupController extends LookupController
{
    @Service('RIVLookupService')
    def svc;

    Column[] getColumns() {
        return [
            new Column(name:"txnno", caption:"RIV No.", width:200),
            new Column(name:"requestedby_name", caption:"Requested By", width:250),
            new Column(name:"type", caption:"Type", width:150),
        ];
    }

    List fetchList(Map params) {
        return svc.getList( params );
    }
}
