import com.rameses.rcp.common.*
import com.rameses.rcp.annotations.*
import com.rameses.osiris2.reports.*

class RISReportController extends ReportModel
{
    private Object reportData;
    private Map parameters;

    public String getReportName() {
        return "com/rameses/gov/etracs/treasury/collection/reports/ReportRIS.jasper";
    }

    public SubReport[] getSubReports() {
        return [ new SubReport("ReportRISItem", "com/rameses/gov.etracs/treasury/collection/reports/ReportRISItem.jasper") ] as SubReport[];
    }

    public Object getReportData() {
        return reportData;
    }

    public void setReportData(Object reportData) {
        this.reportData = reportData;
    }

    public Map getParameters() {
        return parameters;
    }

    public void setParameters(Map parameters) {
        this.parameters = parameters
    }
}
