workunit.code:
import com.rameses.rcp.common.*
import com.rameses.rcp.annotations.*
import com.rameses.osiris2.client.*
import com.rameses.osiris2.common.*

class LookupBarangayController extends LookupController
{ 
    @Service('BarangayLookupService')
    def svc

    def parentid

    Column[] getColumns(){
        return [
            new Column(name:"indexno", caption:"Index No"),
            new Column(name:"name", caption:"Name"),
        ]
    }

    List fetchList(Map params) {
         params.parentid = parentid
         return svc.lookupBarangays( params );
    }

}
