workunit.code:
import com.rameses.osiris2.common.*;
import com.rameses.rcp.common.*;
import com.rameses.rcp.annotations.*;
import com.rameses.osiris2.client.*;
import javax.swing.*;
import com.rameses.io.*;

class AFSerialControlImportController 
{
    @Binding
    def binding;

    @Service('AFSerialControlExportImportService')
    def svc;


    String title = 'Import Accountable Forms';

    def afcontrols;
    def mode;

    def MODE_INIT       = 'init';
    def MODE_FORPOSTING = 'forposting';
    def MODE_POSTED     = 'posted';


    void init(){
        afcontrols = [];
        mode = MODE_INIT;
    }


    void doImport(){
        def chooser = new JFileChooser();
        int i = chooser.showOpenDialog(null);
        if(i==0) {
            afcontrols = FileUtil.readObject( chooser.selectedFile );
            listHandler.reload();
            mode = MODE_FORPOSTING;
        }      
    }

    void post(){
        if (MsgBox.confirm('Post imported controls?')){
            afcontrols.each{ ac ->
                try {
                    svc.postImportedAfControl(ac);
                    ac.status = 'Successfully posted.'
                }
                catch(e ){
                    ac.status = e.message
                }
            }
            listHandler.reload();
            mode = MODE_POSTED;
        }
    }


    def listHandler = [
        fetchList: { return afcontrols; }
    ] as BasicListModel;


}
